import React, { Component } from 'react'
import api from '../../api'
import SimpleMap from '../'

export default class Breweries extends Component {
  constructor(props) {
    super(props)
    this.state = {
      breweries: [],  
      filteredBreweries: [],
    }
  }

render() {
  return (
    <React.Frament>
    <div className="breweries">
      <h2>Breweries (25,000+!)</h2>
      {this.state.breweries.map(c => (
        <li key={c._id}>{c.address}</li>
      ))}
    </div>

    <div className="Breweries">
      {this.state.filteredBreweries.map((eachBrewery, i) => (
        <div key={i} className="eachBrewery">
        <img src={eachBrewery.image_url} alt=""/>
        <ul className="breweryDeets">
        <p key={i+'a'}>Address: {eachBrewery.address}</p>
        <p key={i+'b'}>Location: {eachBrewery.city}</p>, <p key={i+'c'}> {eachBrewery.province}</p>
        <p key={i+'c'}>Website: {eachBrewery.websites}</p>
        </ul>
        <button className='favorite button' onclick="addFavorite()">Add Favorite</button>
    </div>
      ))}
    </div>
    </React.Frament>
  )
}

  componentDidMount() {
    api
      .getBreweries()
      .then(breweries => {
        console.log(breweries)
        this.setState({
          breweries: breweries,
        })
      })
      .catch(err => console.log(err))
  }
}
